import React, {useState} from "react"
import {View, Button, TextInput, ScrollView, StyleSheet} from "react-native"

const CreateUser = (props) => {
    
    const[state, setState]= useState({
        nome:"",
        email:"",
        telefone:"",

    });

    const handleChangeText = (nome, value)=>{
        setState({...state, [nome]: value})
    };

    const saveNewUser = () => {
        if (state.nome === ''){
            alert ("Por favor preencha seu nome");
            props.navigation.navigate('UserList');
        }
    }

    
    return (
        <ScrollView style={styles.container} >
            <View style={styles.inputGroup} >
                <TextInput placeholder="Nome"
                 onChangeText={(value)=> handleChangeText('nome', value) } />
            </View>

            <View style={styles.inputGroup} >
                <TextInput placeholder="Email" 
                onChangeText={(value)=> handleChangeText('email', value) } />
            </View>

            <View style={styles.inputGroup} >
                <TextInput placeholder="Telefone"
                onChangeText={(value)=> handleChangeText('telefone', value) } />
            </View>

            <View style={styles.inputGroup} >
                <Button title="Salvar UsuÃ¡rio" onPress={()=> console.log(state) } />
            </View>

        </ScrollView>
    )
}

const styles = StyleSheet.create({
    container: {
        flex:1,
        padding:35,
    },

    inputGroup: {
        flex:1,
        padding:0,
        margin:15,
        borderBottomWidth:1,
        borderBottomColor: '#28d9f9'
    }    
})

export default CreateUser
